INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('1', NULL, '<p>Sebagai Kepala Sekolah, saya menyadari tanggung jawab besar yang diemban untuk memimpin dan memajukan sekolah ini. Tujuan utama saya adalah untuk terus meningkatkan kualitas pendidikan dan menciptakan lingkungan belajar yang kondusif dan inspiratif bagi seluruh siswa. Saya berkomitmen untuk bekerja sama dengan seluruh pihak&mdash;guru, staf, orang tua, dan masyarakat&mdash;untuk mencapai visi dan misi sekolah yang telah kita tetapkan.</p>', NULL, '0', NULL, 'opening_speech', 'draft', 'public', 'close', NULL, NULL, '0', '2024-04-06 03:50:24', '2024-08-07 10:42:12', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('2', 'Profil', '<p>Nama Sekolah: SMP Negeri 105 Maluku Tengah<br />Tahun Berdiri: 2014<br />Alamat: Kecamatan Tehoru, Kabupaten Maluku Tengah, Provinsi Maluku, Indonesia</p>\n<p>Deskripsi:<br />SMP Negeri 105 Maluku Tengah adalah sekolah menengah pertama yang didirikan pada tahun 2014 di Kecamatan Tehoru, Kabupaten Maluku Tengah. Sekolah ini berkomitmen untuk menyediakan pendidikan berkualitas kepada siswa-siswi di wilayah tersebut dengan fokus pada pengembangan akademis, karakter, dan keterampilan. SMP 105 Maluku Tengah berusaha menciptakan lingkungan belajar yang inklusif dan mendukung, mempersiapkan generasi muda untuk menghadapi tantangan masa depan dengan pengetahuan dan keterampilan yang memadai.</p>', NULL, '1', '+1+', 'page', 'publish', 'public', 'open', 'profil', 'berita, pengumuman, sekilas-info', '98', '2024-04-06 03:50:24', '2024-08-18 23:20:58', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('3', 'Visi dan Misi', '<p>Visi</p>\n<p>\"Berprestasi, Disiplin dan berkualitas dalam Bidang Ilmu Pengetahuan dan teknologi\"</p>\n<p>Misi</p>\n<p>Untuk dapat mewujudkan visi maka misi dari SMP Negeri 105 Maluku Tengah sebagai berikut :</p>\n<ol>\n<li>Meningkatkan nilai-nilai disiplin dalam kehidupan sehari-hari;</li>\n<li>Meningkatkan prestasi akademik dan non akademik;&nbsp;</li>\n<li>Meningkatkan sumber daya pendidikan dan tenaga pendidikan;&nbsp;</li>\n<li>Meningkatkan peran masyarakat dan komite sekolah dalam menciptakan lingkungan pembelajaran yang adam;&nbsp;</li>\n<li>Menumbuhkan nilai-nilai peduli lingkungan;&nbsp;</li>\n<li>Menumbuhkan prestasi dalam bidang ilmu pengetahuan, teknologi, dan seni;</li>\n<li>Meningkatkan menejemen berbasis sekolah.</li>\n</ol>', NULL, '1', '+1+', 'page', 'publish', 'public', 'open', 'visi-dan-misi', 'berita, pengumuman, sekilas-info', '71', '2024-04-06 03:50:24', '2024-08-18 13:39:45', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('4', 'PEMBAGIAN ID CARD KEPADA PESERTA UJIAN', '<p>Acara ini diadakan untuk memastikan setiap peserta ujian memiliki identitas resmi yang akan digunakan selama pelaksanaan ujian. ID Card ini merupakan bukti sah sebagai peserta ujian dan wajib dibawa selama mengikuti ujian di SMP Negeri 105 Maluku Tengah.</p>', '8e5dc53713383dc106c7d1b2ab385e4d.jpeg', '1', '+1+', 'post', 'publish', 'public', 'close', 'pembagian-id-card-kepada-peserta-ujian', 'berita, pengumuman, sekilas-info', '31', '2024-04-06 03:50:24', '2024-08-19 12:21:45', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('5', 'KARNAVAL', '<p>Kegiatan Ini dilakukan untuk menyambut hari ulang tahun kemerdekaan Repoblik Indonesia yang ke 78 tahun. Kegiatan ini dilakukan oleh siswa - siswi SMP NEGERI 105 MALUKU TENGAH.</p>', 'f636e7b7c3dc177cfe652a042c33d9e8.jpeg', '1', '+1+', 'post', 'publish', 'public', 'close', 'karnaval', 'berita, pengumuman, sekilas-info', '22', '2024-04-06 03:50:24', '2024-08-18 00:10:03', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('6', 'KEGIATAN PORSENI (CAKALELE)', '<p><strong>Kegiatan Porseni di SMP Negeri 105 Maluku Tengah</strong></p>\r\n<p>Dalam rangka mengembangkan bakat dan minat siswa-siswi, serta mempererat tali persaudaraan dan kekompakan antar siswa, SMP Negeri 105 Maluku Tengah mengadakan kegiatan Pekan Olahraga dan Seni (Porseni). Kegiatan ini berlangsung dengan penuh semangat dan antusiasme dari seluruh peserta.</p>\r\n<p><strong>Tanggal Pelaksanaan:</strong>&nbsp;4 Maret 2023<br /><strong>Tempat :</strong> SMP Negeri 105 Maluku Tengah</p>\r\n<p>Porseni merupakan ajang bagi siswa untuk menunjukkan kemampuan mereka dalam berbagai cabang olahraga dan seni.</p>', 'dc1bac75b8ea7ce9fbfcad2c59b3ed10.jpeg', '1', '+1+', 'post', 'publish', 'public', 'close', 'kegiatan-porseni-cakalele', 'berita, pengumuman, sekilas-info', '22', '2024-04-06 03:50:24', '2024-08-18 15:31:01', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('7', 'RAPAT PEMBAGIAN RAPOT SISWA/I', '<p>Rapat ini di lakukan pada akhir semester untuk menentukan naik kelas dan tidaknya, sekaligus untuk memberikan hadiah dan apresiasi untuk siswa - siswi yang berprestasi</p>', '274adfa31690a86707ab93445f9dbc1f.jpeg', '1', '+1+', 'post', 'publish', 'public', 'close', 'rapat-pembagian-rapot-siswai', 'berita, pengumuman, sekilas-info', '17', '2024-04-06 03:50:24', '2024-08-19 00:38:53', NULL, NULL, '0', '1', '0', '0', 'false');
INSERT INTO `posts` (`id`, `post_title`, `post_content`, `post_image`, `post_author`, `post_categories`, `post_type`, `post_status`, `post_visibility`, `post_comment_status`, `post_slug`, `post_tags`, `post_counter`, `created_at`, `updated_at`, `deleted_at`, `restored_at`, `created_by`, `updated_by`, `deleted_by`, `restored_by`, `is_deleted`) VALUES ('8', 'STRUKTUR ORGANISASI SEKOLAH SMP NEGERI 105 MALUKU TENGAH', '<p>Struktur ini menggambarkan bagaimana wewenang, tanggung jawab, dan hubungan komunikasi diatur di antara berbagai elemen di dalam sekolah.</p>\r\n<h3><strong>Fungsi Struktur Organisasi&nbsp;</strong></h3>\r\n<ul>\r\n<li><strong>Koordinasi:</strong> Menyusun dan mengatur kegiatan sekolah sehingga semua komponen bekerja secara harmonis.</li>\r\n<li><strong>Pembagian Tugas:</strong> Mendefinisikan tanggung jawab dan wewenang untuk setiap posisi sehingga semua tugas dapat dikelola dengan efisien.</li>\r\n<li><strong>Komunikasi:</strong> Menetapkan saluran komunikasi yang jelas antara berbagai elemen dalam sekolah.</li>\r\n<li><strong>Pengelolaan:</strong> Memastikan bahwa sumber daya, baik manusia maupun material, digunakan secara efektif untuk mencapai tujuan pendidikan.</li>\r\n</ul>\r\n<p>Struktur organisasi ini membantu sekolah dalam menjalankan fungsinya secara teratur, efektif, dan berorientasi pada pencapaian hasil pendidikan yang berkualitas.</p>', '4f3a069412013e3f1580b2ff306515b5.jpeg', '1', '+1+', 'post', 'publish', 'public', 'close', 'struktur-organisasi-sekolah-smp-negeri-105-maluku-tengah', 'berita, pengumuman, sekilas-info', '18', '2024-04-06 03:50:24', '2024-08-18 07:05:35', NULL, NULL, '0', '1', '0', '0', 'false');
